select eventoID from evento where nome = "Jogo de portugal"; # Obter o ID de um evento criado, pois este é auto_increment (uma querry automatica do java)
select eventoZonaID from evento_zona where eventoID_ev_zon = 1 and zonaID_ev_zon = 13; # Obter o ID da tabela de ligação entre o evento e a zona (evento_zona), informação necessária para a criação de bilhetes, pois este é auto_increment (uma querry automatica do java)
SELECT (select sum(lugaresTotalZona)  from zona) - (select count(*)  from ((bilhete inner join evento_zona ON bilhete.eventoZonaID_bilhete=evento_zona.zonaID_ev_zon) inner join evento ON evento.eventoID=evento_zona.eventoID_ev_zon) where eventoID = 1); # Obter o numero de lugares disponiveis para um determinado evento (fizemos com o eventoID = 1 como exemplo, no projeto vai ser usado uma variavel; ainda não está implementado)
select lugaresTotalZona - count(eventoZonaID_bilhete) as "Lugares Disponiveis" from ((bilhete inner join evento_zona ON bilhete.eventoZonaID_bilhete=evento_zona.zonaID_ev_zon) inner join zona ON zona.zonaID=evento_zona.zonaID_ev_zon ) where eventoZonaID_bilhete = 8; # Obter o numero de lugares disponiveis de uma zona para um determinado evento, usando o id da tabela de ligação entre evento e zona (foi usado o eventoZonaID_bilhete = 8 como exemplo, no projeto será usado uma variavel; ainda nao está implementado)
select (taxa * precoBase) from ((zona inner join evento_zona ON zona.zonaID=evento_zona.zonaID_ev_zon) inner join evento ON evento.eventoID=evento_zona.eventoID_ev_zon) where eventoZonaID = 13; # Calcular o preço de uma zona para um determinado evento, usando o id da tabela de ligação entre evento e zona (foi usado o eventoZonaID = 13 como exemplo, no projeto será usado uma variavel; ainda nao está implementado)